$(document).foundation();$(function(){if(Modernizr.touch){$("html").addClass("touch-device");$("a").on({touchstart:function(){$(this).addClass("touched")},touchend:function(){$(this).removeClass("touched");window.location.assign($(this).attr("href"))}})}else $("html").addClass("no-touch");$("a").on("click",function(n){n.preventDefault()})});